package String_m;

public class Jum_break {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

			for(int i=0;i<=8;i++) {
				System.out.println(i);
				if(i==8) {
					break;
				}
				if(i==5) {
					break;
					
				}
				System.out.println("hi dipa");
			}
		
			//System.out.println("hi jva");
			
	}

}
