package control_Stat;

public class Switchcase1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          int A=4;
        switch(A) {
        case 1: System.out.println("value of A is 1");
        break;
        case 2: System.out.println("value of A is 2");
        break;
        case 3: System.out.println("value of A is 3");
        break;
        case 4:System.out.println("value of A is 4");
        break;
        case 5:System.out.println("value of A is 5");
        break;
        default: System.out.println("value not found");
        }
		
		
	}

}
