package loop;

public class HalfPyramid_pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		*
//		* *
//		* * *
//		* * * *s
		int row =4;
		for(int i=1;i<=row;i++) {
			for(int j=1;j<=i;j++) {
				System.out.print(" * ");
			}
		System.out.println();
		}
		
	}

}
