package loop;

public class Inv_half_p_rotateBY180 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
				int	start=4;
			for(int i=1;i<=start;i++) {
				System.out.println("  ");
			
			for(int j=1;j<=start-i;j++) {
				System.out.print("   ");
			}
			for(int x=1;x<=i;x++) {
				System.out.print(" * ");
			}
	}
	
	
	}

}